import { useRouter } from "next/router";
import { Layout } from "../../components/Layout/Layout";
import { getCharacterBySlug } from "../../client";
const CharacterPage = ({
  id,
  gender,
  titles,
  name,
  allegiance,
  slug,
  image,
  alive,
  spouse,
  placeOfDeath,
  death,
  children,
  pagerank,
  house,
  culture,
  birth
}) => {
  return (
    <Layout>
      <h1>
        {titles[0]} {name} Of {house}
      </h1>
      <p>he is {gender}</p>
      <p>he was born in {birth} A.C</p>
      <p>he is {culture}</p>
      <p>{alive ? `he still alive` : `he passed away`} </p>
      <p>
        he {alive ? "is" : "was"} married to {spouse.map(spous => `${spous}, `)}
      </p>
      <p>
        he has {children.length} children :
        {children.map(child => (
          <span>{child}, </span>
        ))}
      </p>
    </Layout>
  );
};
CharacterPage.getInitialProps = async function(context) {
  const { slug } = context.query;
  const character = await getCharacterBySlug(slug);
  return character;
};
export default CharacterPage;
